import React, { useRef } from 'react'
import './navBar.css';

const NavBar = () => {
  const navRef=useRef();
  const navBarControl=()=>{
    navRef.current.classList.toggle('show');
  }
  return (
    <>
        <nav>
              <div className='container'>
                       <a href="#" className='brand'>Ar$@l@n $ultan</a>
                          <ul className='nav-menu' ref={navRef}>
                                 <li><a href="/add">Home</a></li>
                                 <li><a href="#">Courses</a></li>
                                 <li><a href="#">Features</a></li>
                                 <li><a href="#">Testimonals</a></li>
                                 <li><a href="#">Subscribe</a></li>
                                <li className='login'><a href="#">Login</a></li>
                                <li className='signup'><a href="#">Signup</a></li>





                                   <div className='close_btn'>
                                       <span className='material-symbols-outlined' onClick={navBarControl}>close</span>
                                  </div>
                          </ul>
                </div>




        <div className='btn_toggle'>
         <span className='material-symbols-outlined' onClick={navBarControl}>sort</span>
       </div>

        </nav>
    </>
        )
}

export default NavBar;
